// Extract data sections
const cash = $input.all()[0].json.output.cash;
const data = $input.all()[1].json.output.data;
const prices = $input.all()[2].json.stock_prices;

// Create a lookup map for prices by symbol
const priceMap = {};
for (const p of prices) {
  priceMap[p.symbol] = p;
}

// Merge data with prices
const merged = data.map(d => {
  const p = priceMap[d.Symbol] || {};
  return {
    name: d.Name,
    symbol: d.Symbol,
    reason: d.Reason,
    expected_change_1d: d["Expected Change 1 Day"],
    certainty_1d: d["Certainty 1 Day"],
    expected_change_1w: d["Expected Change 1 Week"],
    certainty_1w: d["Certainty 1 Week"],
    expected_change_1m: d["Expected Change 1 Month"],
    certainty_1m: d["Certainty 1 Month"],
    current: p.current ?? null,
    change: p.change ?? null,
    percent_change: p.percent_change ?? null,
    high: p.high ?? null,
    low: p.low ?? null,
    open: p.open ?? null,
    previous_close: p.previous_close ?? null,
    timestamp: p.timestamp ?? null
  };
});

return [
  {
    json: {
      cash,
      merged
    }
  }
];
